mapper.update:
update hotel_reservation
set hotelCode = #{hotelCode}, hotelRoomNum = #{hotelRoomNum}, hotelCheckIn = #{hotelCheckIn}, hotelCheckOut = #{hotelCheckOut}
where hotelId = #{hotelId}

mapper.select:

select * from hotel_reservation
where hotelCode = #{hotelCode} and hotelRoomNum = #{hotelRoomNum}
and hotelCheckIn >= #{hotelCheckIn} and hotelCheckOut <= #{hotelCheckOut}
